public class CoinReceivedNotifyServiceTest extends BaseTest{

    private static Logger logger = LoggerFactory.getLogger(CoinReceivedNotifyServiceTest.class);

    @Reference
    private IDubboChainRouter iDubboChainRouter;

    @Autowired
    private CoinReceivedNotifyService coinReceivedNotifyService;

    @Autowired
    private EthereumService ethereumService;

    @Autowired
    private SmartContractManage smartContractManage;

    @Test
    public void test()
    {
        String password = JedisUtils.getObjectByRawkey(CommConstants.CHAINROUTER_WALLET_+CommConstants.MANUFACTOR_TYPE.Ethereum.name());
        String source =  JedisUtils.getObjectByRawkey(CommConstants.CHAINROUTER_WALLET_+CommConstants.MANUFACTOR_TYPE.Ethereum.name()+"Source");

        System.out.println(password+source);

        String contractAddress = JedisUtils.getObjectByRawkey(CommConstants.CHAINROUTER_WALLET_+"ERC20:SuperCoin");
        System.out.println(contractAddress);

//        JedisUtils.putMap(CommConstants.CHAINROUTER_WALLET_+CommConstants.MANUFACTOR_TYPE.Ethereum.name()+"ContractMap","");
//        JedisUtils.putMap(CommConstants.CHAINROUTER_WALLET_+CommConstants.MANUFACTOR_TYPE.Ethereum.name()+"ContractMap","EOS","");
        Map map = JedisUtils.getMap(CommConstants.CHAINROUTER_WALLET_+CommConstants.MANUFACTOR_TYPE.Ethereum.name()+"ContractMap");

        Iterator<Map.Entry<Object, Object>> it = map.entrySet().iterator();
        while (it.hasNext()) {
            Map.Entry<Object, Object> entry = it.next();
            System.out.println("key= " + entry.getKey() + " and value= " + entry.getValue());
        }
    }

    public static void main(String[] args)
    {
        String eth =Convert.fromWei("5000000000000000000000", Convert.Unit.ETHER).toPlainString();
        System.err.println(eth);
    }

    @Test
    public void transfer()
    {
        try {

            // 调用转账方法
            TransactionReceipt receipt = smartContractManage.getEOSsolDSToken("SuperCoin").transfer("0x0217E9fA458c54A2CdF4dF456cfC5d2033aBBD54", BigInteger.valueOf(3)).send();

            // 打印交易Hash
            System.out.println(receipt.getTransactionHash());

        }catch (Exception e)
        {
            System.err.println(e.getMessage());
        }
    }

    @Test
    public void getBalance()
    {

        try {
            BigInteger value = toWei("0.1", Convert.Unit.ETHER).toBigInteger();
            System.err.println(value);

            String eth =Convert.fromWei("100000000000000000", Convert.Unit.ETHER).toPlainString();
            System.err.println(eth);
            Results results = ethereumService.getBalance("");

            logger.info("response data:{}", JacksonUtil.getJackson(results));

        }catch (Exception e)
        {
            logger.error(e.getMessage());
        }
    }

    @Test
    public void notifyCoinRecevied()
    {
        coinReceivedNotifyService.notifyCoinRecevied("mo2hwxU1TwbcrGyYaXTqhpiEpzpuEHdHuP", CommConstants.MANUFACTOR_TYPE.BitCoin.value(),"095296264a4e802c5849cd00a89dcd3ec648a9f6e37814885aa11f354d3050bc","0.01");
    }

    @Test
    public void withdrawalCash()
    {
        Map<String, Object> searchParams = new HashMap<>();
        searchParams.put("accountName","lzf");
        searchParams.put("accountAddress","0x0217E9fA458c54A2CdF4dF456cfC5d2033aBBD54");
        searchParams.put("txAmount","3");
        searchParams.put("txFee","0");
        searchParams.put("comment","");
        searchParams.put("commentTo","SuperCoin");

        Results results = ethereumService.withdrawalCash(JavaReflectUtil.obj2str(searchParams.get("accountName")),JavaReflectUtil.obj2str(searchParams.get("accountAddress")),
                JavaReflectUtil.obj2str(searchParams.get("txAmount")),JavaReflectUtil.obj2str(searchParams.get("txFee")),
                JavaReflectUtil.obj2str(searchParams.get("comment")),JavaReflectUtil.obj2str(searchParams.get("commentTo")));

        logger.info("response data:{}", JacksonUtil.getJackson(results));
    }
}
